// HTTP REQUEST
const fetch_stocks = async () => {
  let response = await fetch(
    'https://dumbstockapi.com/stock?exchanges=NYSE'
  );
  let json = await response.json();
  return json;
}





// splits fetched json data array into chunks that are going to be requested whenever user scrolls


let lastItem = '';
export const fetch_rendered_data = async qty => {
  let arr = fetch_stocks()
    .then(
      async (arr_object) => {

        return new Promise( async (resolve, reject) => {
          let newArr;
          const lastItemIndex = arr_object.indexOf(lastItem);
          if (lastItemIndex === arr_object.length - 1) return resolve('done');

          if (!lastItem) {
            newArr = [...arr_object].slice(0, qty);
            lastItem = [...newArr].pop();
          } else {
            const newIndex = arr_object.indexOf(lastItem) + 1;
            newArr = [...arr_object].slice(newIndex, qty + newIndex);
            lastItem = [...newArr].pop();
          }
          setTimeout(() => {
            resolve(newArr);
          }, 1000);
          console.log(newArr)
        });
      }
    )
}
