import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useState } from 'react';
import { FlatList, StyleSheet, Text, View, TouchableOpacity , TextInput} from 'react-native';
import { fetch_rendered_data, fetch_stocks } from '../DataServer';
import { useNavigation } from '@react-navigation/core';
import SearchComponent from './SearchComponent';


// check to halt load requests
let stopFetchMore = true;



// LOADING VIEW
const ListFooterComponent = () => (
    <Text
        style={{
            fontSize: 16,
            fontWeight: 'bold',
            textAlign: 'center',
            padding: 5,
        }}
    >
        Loading...
    </Text>
);




// MAIN COMPONENT
const ScrollLoad = () => {

    const navigation = useNavigation()

    // DATA ARRAY ITEM VIEW
    const renderItem = ({ item }) => {
        return (
            <TouchableOpacity
                onPress={() => {
                    navigation.navigate("Vote", { data: item })
                }}
                style={styles.stock_button}
                activeOpacity={0.25}
            >
                <Text title={styles.stuck_button_text} >
                    {item.ticker}  : {item.name}
                </Text>
            </TouchableOpacity>
        );
    };


    // states , fetch data and init variables
    const quantity_rendered = 20;
    const [data, setData] = useState([]);
    const [render_index, set_render_index] = useState(0);
    const [loadingMore, setLoadingMore] = useState(false);

    const updateData = async () => {
        set_render_index(render_index + quantity_rendered)
        // gets data from server.js
        const response = await fetch_rendered_data(quantity_rendered, render_index)
        // check if we got data from DataServer.js
        if (response) setData([...response]);
        else setData(["error", "DataServer.js", "didnt return the promise"])
    };

    // on page render
    useEffect(() => {
        updateData()
    }, []);



    // load more unless server resolves done
    const handleOnEndReached = async () => {
        setLoadingMore(true);
        if (!stopFetchMore) {
            const response = await fetch_rendered_data(quantity_rendered, render_index); //fetch
            if (response === 'done') return setLoadingMore(false);
            stopFetchMore = true;
            setData([...data, ...response]); // append to [state list]
            set_render_index(render_index + quantity_rendered) // add 20 to last index
        }
        setLoadingMore(false);
    };

    const ItemView = ({ item }) => {
        return (
            // Flat List Item
            <Text style={styles.itemStyle} onPress={() => getItem(item)}>
                {item.id}
                {'.'}
                {item.title.toUpperCase()}
            </Text>
        );
    };

    const ItemSeparatorView = () => {
        return (
            // Flat List Item Separator
            <View
                style={{
                    height: 0.5,
                    width: '100%',
                    backgroundColor: '#C8C8C8',
                }}
            />
        );
    };

    const getItem = (item) => {
        // Function for click on an item
        alert('Id : ' + item.id + ' Title : ' + item.title);
    };









    // SEARCH BAR

    const searchFilterFunction = (text) => {
        // Check if searched text is not blank
        if (text) {
            // Inserted text is not blank
            // Filter the masterDataSource and update FilteredDataSource
            const newData = data.filter(function (item) {
                // Applying filter for the inserted text in search bar
                const itemData = item.ticker
                    ? item.ticker.toUpperCase()
                    : ''.toUpperCase();
                const textData = text.toUpperCase();
                return itemData.indexOf(textData) > -1;
            });
            setFilteredDataSource(newData);
            setSearch(text);
        } else {
            // Inserted text is blank
            // Update FilteredDataSource with masterDataSource
            setFilteredDataSource(masterDataSource);
            setSearch(text);
        }
    };





    return (
        <View>



            {/* SEARCH */}
            <View style={styles.container}>
                <TextInput
                    style={styles.textInputStyle}
                    onChangeText={(text) => searchFilterFunction(text)}
                    value={search}
                    underlineColorAndroid="transparent"
                    placeholder="Search Here"
                />
                {/* <FlatList
          data={filteredDataSource}
          keyExtractor={(item, index) => index.toString()}
          ItemSeparatorComponent={ItemSeparatorView}
          renderItem={ItemView}
        /> */}
            </View>






            {/* DISPLAY */}
            <FlatList
                data={data}
                keyExtractor={item => data.indexOf(item)}
                renderItem={renderItem}
                onEndReached={handleOnEndReached}
                onEndReachedThreshold={0.01}
                onScrollBeginDrag={() => {
                    stopFetchMore = false;
                }}
                ListFooterComponent={() => loadingMore && <ListFooterComponent />}
            // onScroll={(event) => console.log(event)}
            />


        </View>

    );
}









const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        margin: 50
    },
    stock_button: {
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: 20,
        padding: 15,
        borderBottomColor: 'red',
        borderBottomWidth: 2,
    },
    stuck_button_text: {
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: 20,
        padding: 15,
        borderBottomColor: 'red',
        borderBottomWidth: 2,
    },

    itemStyle: {
        padding: 10,
    },
    textInputStyle: {
        height: 40,
        borderWidth: 1,
        paddingLeft: 20,
        margin: 5,
        borderColor: '#009688',
        backgroundColor: '#FFFFFF',
    },


});




export default ScrollLoad
