import { StyleSheet, Text, TouchableOpacity, View, ScrollView } from 'react-native'
import React, { useEffect, useState } from 'react'

const CONFIG = require("../api_config/config.json")









const WeekExpirationDisplay = () => {

    const [timeRN, setTimeRN] = useState(new Date().toLocaleString())
    const [experation_date, setExperationDate] = useState('')


    const fetchDate = () => {
        fetch(`${CONFIG.base_url}/api/vote_experation_date`)
            .then((response) => response.json())
            .then((json) => {
                // format date
                let date_obj = Date.parse( json.data )
                setExperationDate(date_obj.toString())
            })
            .catch((error) => console.error(error))
    }


    // on page render
    useEffect(() => {
        fetchDate();
    }, []);





    return (

        <View style={styles.time_display}>
            <Text style={styles.experation_text}> EXPERATION DATE </Text>
            <Text style={styles.experation_text}> {experation_date.length > 0 ? experation_date : "Connection Error"}  </Text>
        </View>
    )
}


const styles = StyleSheet.create(
    {
        time_display: {
            marginTop: "50%",
            justifyContent: "center",
            alignItems: "center",
        },
        experation_text: {
            fontWeight: 'bold',
            fontSize: 25
        }
    }
)





export default WeekExpirationDisplay
