import { StyleSheet, Text, TouchableOpacity, View, ScrollView } from 'react-native'
import React, { useEffect, useState } from 'react'







const WeekExpirationDisplay = (props) => {
    
    const timeRN = new Date().toLocaleString()
    

    function next_week() {
        var today = new Date();
        var nextweek = new Date(today.getFullYear(), today.getMonth(), today.getDate()+7);
        return nextweek;
    }
    
    
    
    // build api that gives expiration dates and saves voter data
    const fb_db = props.db
    var expiration_date = fb_db.ref("expiration_date")
    console.log(expiration_date)


    
    
    return (

        <View style={styles.time_display}>

            <Text> CURRENT:  {timeRN}   </Text>
            <Text> // build api that gives expiration dates and saves voter data  </Text>

        </View>


       

        )

}


const styles = StyleSheet.create(
    {
      time_display: {
        marginTop: "66%",
        justifyContent: "center",
        alignItems: "center",
      },
    }
  )
  




export default WeekExpirationDisplay
