import { StyleSheet, Text, TouchableOpacity, View, ScrollView , SafeAreaView } from 'react-native'
import React, { useEffect, useState } from 'react'
import { useNavigation } from '@react-navigation/core';

import ScrollLoad from '../components/ScrollLoad';
import WeekExpirationDisplay from '../components/WeekExpirationDisplay';
import StockList from '../components/StockList';
import SwipeCardStack from '../components/SwipeCardStack';
import { auth } from "../api_config/firebase";




const HomeScreen = () => {

  const navigation = useNavigation()

  const handleSignOut = () => {
    auth
      .signOut()
      .then(() => {
        navigation.replace("Login")
      })
      .catch(error => { alert(error.message) })
  }



  return (
    <View style={styles.container}>

      {/* DISPLAY TIME */}
      <WeekExpirationDisplay />




      {/* SWIPING VIEW */}
      <SwipeCardStack />


      {/* SIGN OUT */}
      <View style={styles.bottomView}>
        <Text>Email: {auth.currentUser?.email} </Text>
        <TouchableOpacity style={styles.button} onPress={handleSignOut}>
          <Text style={styles.buttonText}>Sign Out</Text>
        </TouchableOpacity>
      </View>

      <SafeAreaView style={{ flex: 1 }}>
        <View style={styles.containerMain}>
          <Text> Main Content Here</Text>
          <View style={styles.bottomView}>
            <Text style={styles.textStyle}>Bottom View</Text>
          </View>
        </View>
      </SafeAreaView>

    </View>
  )




  // return (
  //   <View style={styles.container}>

  //     {/* DISPLAY TIME */}
  //     <WeekExpirationDisplay  />







  //     {/* DISPLAY TICKERS AND ROUTE TO THEIR VOTE CARD */}
  //     {/* <ScrollLoad/> */}
  //     {/* <StockList/> */}








  //     {/* SIGN OUT */}
  //     <Text>Email: {auth.currentUser?.email} </Text>
  //     <TouchableOpacity style={styles.button} onPress={handleSignOut}>
  //       <Text style={styles.buttonText}>Sign Out</Text>
  //     </TouchableOpacity>



  //   </View>
  // )
}






const styles = StyleSheet.create(
  {
    container: {
      flex: 1,
      alignItems: 'center',
      justifyContent: 'center',
    },
    time_display: {
      backgroundColor: "black",
      justifyContent: "center",
      alignItems: "center",
    },
    button: {
      backgroundColor: "#0782F9",
      width: "60%%",
      padding: 15,
      borderRadius: 10,
      alignItems: "center",
      marginTop: 40
    },
    buttonText: {
      flex: 1,
      color: "white",
      fontWeight: "700",
      fontSize: 16,
    },

    bottomView: {
      width: '100%',
      height: 50,
      backgroundColor: '#EE5407',
      justifyContent: 'center',
      alignItems: 'center',
      position: 'absolute', //Here is the trick
      bottom: 0, //Here is the trick
    }



  }
)






export default HomeScreen
