import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useState } from 'react';
import { FlatList, StyleSheet, Text, View } from 'react-native';
import { fetch_rendered_data } from '../DataServer';



// check to halt load requests
let stopFetchMore = true;



// DATA ARRAY ITEM VIEW
const renderItem = ({ item }) => {
    console.log(item)
    return (
        <Text
            style={{
                textAlign: 'center',
                fontWeight: 'bold',
                fontSize: 20,
                padding: 15,
                borderBottomColor: 'red',
                borderBottomWidth: 2,
            }}
        >
            {item.ticker }  : {item.name}
        </Text>
    );
};
// LOADING VIEW
const ListFooterComponent = () => (
    <Text
        style={{
            fontSize: 16,
            fontWeight: 'bold',
            textAlign: 'center',
            padding: 5,
        }}
    >
        Loading...
    </Text>
);











// MAIN COMPONENT
const ScrollLoad = () => {


    // states , fetch data and init variables
    const quantity_rendered = 20;
    const [data, setData] = useState([]);
    const [loadingMore, setLoadingMore] = useState(false);

    const updateData = async () => {
        const response = await fetch_rendered_data(quantity_rendered) // gets data from server.js
        // check if we got data from DataServer.js
        if (response) setData([...response]);
        else setData(["error", "DataServer.js", "didnt return the promise"])
    };

    // on page render
    useEffect(() => {
        updateData()
    }, []);



    // load more unless server resolves done
    const handleOnEndReached = async () => {
        setLoadingMore(true);
        if (!stopFetchMore) {
            const response = await fetch_rendered_data(quantity_rendered);
            if (response === 'done') return setLoadingMore(false);
            setData([...data, ...response]);
            stopFetchMore = true;
        }
        setLoadingMore(false);
    };



    return (
        <FlatList
            data={data}
            keyExtractor={item => item.ticker}
            renderItem={renderItem}
            onEndReached={handleOnEndReached}
            onEndReachedThreshold={0.5}
            onScrollBeginDrag={() => {
                stopFetchMore = false;
            }}
            ListFooterComponent={() => loadingMore && <ListFooterComponent />}
        />
    );
}









const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        margin: 50
    },
});




export default ScrollLoad
