// HTTP REQUEST
const fetch_stocks = async () => {
  let response = await fetch(
    'https://dumbstockapi.com/stock?exchanges=NYSE'
  );
  let json = await response.json();
  return json;
}



// CHUNK API REQUEST DATA 

var lastItem = '';
const chunking = (qty, arr_object , index ) => {
  console.log(index)
  let newArr;
  const lastItemIndex = arr_object.indexOf(lastItem);

  // check if list complete
  if (lastItemIndex === arr_object.length - 1) return ["done"];

  // INIT all_stocks state
  if (!lastItem) {
    console.log('>>>>>> onEvent() rendered - initializing [all_stocks state] ')
    newArr = [...arr_object].slice(0, qty);
    lastItem = [...newArr].pop();
  }
  
  // UPDATE all_stocks state
  else {
    console.log('<<<<<< updating [all_stocks state]')
    const newIndex = arr_object.indexOf(lastItem) + 1;
    newArr = [...arr_object].slice(newIndex, qty + newIndex);
    lastItem = [...arr_object].pop();
  }

  return newArr

}




export const fetch_rendered_data = (qty , index) => {
  console.log(qty)
  const arr = fetch_stocks()
    .then(promise_object => chunking(qty, promise_object , index))
  // console.log(arr)
  return arr;
}
