// HTTP REQUEST
const fetch_stocks = async () => {
  let response = await fetch(
    'https://dumbstockapi.com/stock?exchanges=NYSE'
  );
  let json = await response.json();
  return json;
}





// splits fetched json data array into chunks that are going to be requested whenever user scrolls

let lastItem = '';
export const fetch_rendered_data = async (qty) => {
  fetch_stocks()
    .then(
      async (arr) => {
        console.log(arr)
        new Promise(async (resolve, reject) => {
          let newArr;
          const lastItemIndex = arr.indexOf(lastItem);
          if (lastItemIndex === arr.length - 1) return resolve('done');

          if (!lastItem) {
            newArr = [...arr].slice(0, qty);
            lastItem = [...newArr].pop();
          } else {
            const newIndex = arr.indexOf(lastItem) + 1;
            newArr = [...arr].slice(newIndex, qty + newIndex);
            lastItem = [...newArr].pop();
          }
          setTimeout(async () => {
            resolve(newArr);
          }, 1000);
        });

      }
    )
}
